    constEffects: [{
      action: function (set,add) {
        var effect = this.game.newEffect({
          source: this,
          description: '353-const-0',
          // triggerCondition: function (event) {
          //  if (!inArr(this,this.player.signis)) return false;
          //  return true;
          // },
          condition: function (event) {
            if (!inArr(this,this.player.signis)) return false;
            var card = event.card;
            if (card.type !== 'SIGNI') return false;
            var opposingSigni = card.getOpposingSigni();
            if (!opposingSigni) return false;
            return (card.power < opposingSigni.power);
          },
          actionAsyn: function (event) {
            if (!inArr(event.card,event.card.player.signis)) return;
            return event.card.banishAsyn();
          }
        });
        add(this.player.opponent,'onAttack',effect);
      }
    }],
    // ======================
    //        出场效果       
    // ======================
