          priority: 3,
          source: this,
          description: '738-attached-3',
          actionAsyn: function () {
            var cards = concat(this.player.mainDeck.getTopCards(4),this.player.opponent.mainDeck.getTopCards(4));
            this.game.trashCards(cards);
          }
