    artsEffectTexts: [
      "ターン終了時まで、あなたのルリグは「このルリグがアタックしたとき、このターンに《ディストラクト・アウト》以外のアーツを使用していない場合、このルリグの下からカードを好きな枚数ルリグトラッシュに置く。この方法でルリグトラッシュに置かれたカード１枚につき、すべてのプレイヤーは自分のデッキの上からカードを５枚トラッシュに置く。その後、あなたのデッキが０枚の場合、このルリグをアップする。」を得る。"
    ],
    artsEffect: {
      actionAsyn: function () {
        var effect = this.game.newEffect({
          source: this.player.lrig,
          description: '1526-attached-0',
          condition: function () {
            return !this.game.getData(this.player,'flagDestructOut');
          },
          actionAsyn: function () {
            var cards = this.player.lrigZone.cards.slice(1);
            return this.player.selectSomeAsyn('TARGET',cards).callback(this,function (cards) {
              var count = cards.length * 5;
              this.game.frameStart();
              this.game.trashCards(cards);
              cards = concat(this.player.mainDeck.getTopCards(count),
                             this.player.opponent.mainDeck.getTopCards(count));
              this.game.trashCards(cards);
              this.game.frameEnd();
              if (this.player.mainDeck.cards.length) return;
              return this.upAsyn();
            });
          }
        });
        this.game.tillTurnEndAdd(this,this.player.lrig,'onAttack',effect);
      }
    },
    // ======================
