    constEffectTexts: [
      "【常時能力】：レゾナではないあなたのシグニ１体が対戦相手のライフクロス１枚をクラッシュしたとき、このシグニをアップする。"
    ],
    constEffects: [{
      action: function (set,add) {
        var effect = this.game.newEffect({
          source: this,
          description: '1791-const-0',
          triggerCondition: function (event) {
            if (!inArr(event.source,this.player.signis)) return false;
            if (event.source.resona) return false;
            return true;
          },
          condition: function () {
            return !this.isUp;
          },
          actionAsyn: function () {
            return this.upAsyn();
          }
        });
        add(this.player.opponent,'onCrash',effect);
      }
    }],
    // ======================
    //        出场效果       
